mapper.select:
select * from (select t.teacher_id,t.kinder_id,t.teacher_name,t.teacher_regtime, r.role_name from tbl_teachers t inner join tbl_role r on r.role_id = t.rid union all select h.healther_id,h.kid,h.healther_name,h.healther_regtime, r.role_name from tbl_healther h inner join tbl_role r on r.role_id = h.rid union all select s.security_id,s.kid,s.security_name,s.security_regtime, r.role_name from tbl_security s inner join tbl_role r on r.role_id = s.rid) as allt
allt.kinder_id = ${condition.kinderId}
and allt.teacher_name like concat('%',#{condition.teacherName},'%')
order by role_name desc limit ${(curPage-1)*pageSize},${pageSize}

select count(*) from (select t.teacher_id,t.kinder_id,t.teacher_name,t.teacher_regtime, r.role_name from tbl_teachers t inner join tbl_role r on r.role_id = t.rid union all select h.healther_id,h.kid,h.healther_name,h.healther_regtime, r.role_name from tbl_healther h inner join tbl_role r on r.role_id = h.rid union all select s.security_id,s.kid,s.security_name,s.security_regtime, r.role_name from tbl_security s inner join tbl_role r on r.role_id = s.rid) as allt
and allt.kinder_id = #{kinderId}
and allt.teacher_name=#{teacherName}

select * from tbl_students


        and kinder_id = #{condition.kinderId}


        and student_name like concat('%',#{condition.studentName},'%')


        and student_time >= #{condition.startDate}


        and student_time <= #{condition.endDate}


order by student_time limit ${(curPage-1)*pageSize},${pageSize}

select count(*) from tbl_students


        and student_name like concat('%',#{condition.studentName},'%')


        and student_time >= #{condition.startDate}


        and student_time <= #{condition.endDate}

select
s.student_id,p.parents_id,p.parents_name,s.student_name,tps.relation_name,p.parents_tel,p.job,p.parents_regtime
from tbl_parents p
left join tbl_parents_students tps on p.parents_id = tps.parents_id
left join tbl_students s on s.student_id =tps.students_id


        and p.kinder_id = #{condition.kinderId}


        and p.parents_name like concat('%',#{condition.parentsName},'%')


        and p.parents_regtime >= #{condition.startDate}


        and p.parents_regtime <= #{condition.endDate}


limit ${(curPage-1)*pageSize},${pageSize}

select count(*) from
(select p.parents_id,p.parents_name,s.student_name,tps.relation_name,p.parents_tel,p.job,p.parents_regtime
from tbl_parents p
left join tbl_parents_students tps on p.parents_id = tps.parents_id
left join tbl_students s on s.student_id =tps.students_id


        and p.kinder_id = #{kinderId}


) as pr


        and pr.parents_name like concat('%',#{parentsName},'%')


        and pr.parents_regtime >= #{startDate}


        and pr.parents_regtime <= #{endDate}

select * from tbl_staff_attendance


        and kinder_id = #{condition.kinderId}


        and sa_staffname like concat('%',#{condition.saStaffname},'%')


        and sa_rolename = #{condition.saRolename}


order by sa_timescope limit ${(curPage-1)*pageSize},${pageSize}

select count(*) from tbl_staff_attendance


        and kinder_id = #{kinderId}


        and sa_staffname like concat('%',#{saStaffname},'%')


        and sa_rolename = #{saRolename}

select * from tbl_staff_attendance


